(function( exports, $ ) {
    var api = sedApp.editor;

    $( function() {

        $('.module.pinterest-module').livequery(function(){
            var id = $(this).attr("id");
            $(this).on("sed.moduleResize sed.moduleResizeStop" , function(){
                api.contentBuilder.refreshModule( id );
            });

        });

    });


}(sedApp, jQuery));
