(function( exports, $ ) {
    var api         = sedApp.editor;

    $( function() {

        api.moduleAttrUpdate.woo_number_columns = function( elementId , attrValue ){
            api.fn.setNumberColumns( attrValue , $("#" + elementId) );
        };

        api( "woo_number_columns" , function( value ) {
            value.bind( function( to ) {
                var $element    = $('[data-sed-woo-archive-role="posts-container"]');
                api.fn.setNumberColumns( to , $element );
            });
        });


        api.moduleAttrUpdate.woo_product_spacing = function( elementId , attrValue ){
            api.fn.setSpacing( attrValue , $("#" + elementId)  );
            
        };

        api( "woo_product_spacing" , function( value ) {
            value.bind( function( to ) {
                var $element    = $('[data-sed-woo-archive-role="posts-container"]');
                api.fn.setSpacing( to , $element );
            });
        });

        api.moduleAttrUpdate.woo_product_boundary = function( elementId , attrValue ){    //console.log( "woo_product_boundary ----- " , attrValue ); 
            api.fn.setBoundary( attrValue , $("#" + elementId) );
        };

        api( "woo_product_boundary" , function( value ) {
            value.bind( function( to ) {
                var $element    = $('[data-sed-woo-archive-role="posts-container"]');
                api.fn.setBoundary( to , $element );
            });
        });

    });

}(sedApp, jQuery));