(function( exports, $ ) {
    var api         = sedApp.editor;

    $( function() {


        api( "cross_sells_columns" , function( value ) {
            value.bind( function( to ) {
                var $element    = $('#woo-cross-sells-products');
                api.fn.setNumberColumns( to , $element );
            });
        });

        api( "cross_sells_product_spacing" , function( value ) {
            value.bind( function( to ) {
                var $element    = $('#woo-cross-sells-products');
                api.fn.setSpacing( to , $element );
            });
        });

        api( "cross_sells_product_boundary" , function( value ) {
            value.bind( function( to ) {
                var $element    = $('#woo-cross-sells-products');
                api.fn.setBoundary( to , $element );
            });
        });

    });

}(sedApp, jQuery));