(function( exports, $ ){
    
    var api = sedApp.editor;



    $( function() { 

        api( 'site_custom_css', function( value ) {
            value.bind( function( to ) {

                //sanitize string => remove html tags
                to = to.replace(/(<([^>]+)>)/ig,"");

                $("#sed_site_custom_css_code").html( to );

            });
        });

        api( 'page_custom_css_code', function( value ) {
            value.bind( function( to ) {

                //sanitize string => remove html tags
                to = to.replace(/(<([^>]+)>)/ig,"");

                $("#sed_page_custom_css_code").html( to );

            });
        });
        
    });


})( sedApp, jQuery );