(function( exports, $ ) {
    var api = sedApp.editor;

    $( function() {
        api.shortcodeUpdate.sed_page_nav = {
            align_icons : function( id , value ){

                var $element = $('[sed_model_id="' + id + '"]');
                    classes = ["ta-l" , "ta-r","ta-c"];

                $.each( classes , function( index , clss ){
                    if( $element.hasClass( clss ) && clss != value )
                        $element.removeClass( clss );
                    else if( !$element.hasClass( clss ) && clss == value )
                        $element.addClass( value );
                });

            },
        };
    })

}(sedApp, jQuery));