(function( exports, $ ) {
    var api = sedApp.editor;

    $( document ).ready( function (  ) {
        $('.portfolio-tabs a').livequery(function(){
           $(this).click(function(e){
          		e.preventDefault();

          		var selector = $(this).attr('data-filter');
          		$(this).parents('.portfolios').find('.portfolio-wrapper').isotope({ filter: selector });

          		$(this).parents('ul').find('li').removeClass('active');
          		$(this).parent().addClass('active');
              });

              /*$(this).parents('.portfolios').find('.portfolio-wrapper').parent().bind( "sed.portfolioAjaxLoaded", function(){
                  var selector = $('.portfolio-tabs a.active').attr('data-filter');
                  $(this).parents('.portfolios').find('.portfolio-wrapper').isotope({ filter: selector });
              });*/

        });
    });

}(sedApp, jQuery));