(function( exports, $ ) {
    var api         = sedApp.editor,
        $element    = $('[data-sed-post-role="post-module-container"]'),
        display_element     = {
            single_post_show_featured_image             : ".single-post-featured-image",
            single_post_title_show                      : ".sed-title-posts",
            single_post_author_show                     : ".post-author",
            single_post_date_show                       : ".post-date",
            single_post_comment_count_show              : ".post-comments",
            single_post_cat_show                        : ".post-cats",
            single_post_tags_show                       : ".post-tags",
            single_post_meta_show                       : ".item-meta",
            single_post_show_related_posts              : ".module.related-post",
            single_post_show_social_share_box           : ".module.posts-share",
            single_post_show_post_nav                   : ".module.posts-nav",
            single_post_show_author_info_box            : ".module.about-author",
            single_post_show_comments                   : ".module-comments",
        };
        display_spr_element     = {
            single_post_separator_show                  : "hide-separator-post",
            single_post_show_related_posts              : "hide-spr-p-related",
            single_post_show_social_share_box           : "hide-spr-p-share",
            single_post_show_post_nav                   : "hide-spr-p-nav",
            single_post_show_author_info_box            : "hide-spr-p-author",
            single_post_show_comments                   : "hide-spr-p-comments",
        };


    $( function() {
       // display and hide item post meta
        $.each( display_element , function( key , selector ) {
            api( key , function( value ) {
                value.bind( function( to ) {

                        if( to )
                            $element.find(selector).removeClass("hide");
                        else
                            $element.find(selector).addClass("hide");

                });
            });
        });

        $.each( display_spr_element , function( key , $class ) {
            api( key , function( value ) {
                value.bind( function( to ) {
                        if( to )
                            $element.removeClass($class);
                        else
                            $element.addClass($class);
                });
            });
        });
        
        api( 'single_post_featured_image_align' , function( value ) {
            value.bind( function( to ) {

                var classes = ["featured-image-left" , "featured-image-right","featured-image-center"];

                $.each( classes , function( index , clss ){
                    if( $element.find(".thumb").hasClass( clss ) && clss != to )
                        $element.find(".thumb").removeClass( clss );
                    else if( !$element.find(".thumb").hasClass( clss ) && clss == to )
                        $element.find(".thumb").addClass( to );
                });

            });
        });

    });
}(sedApp, jQuery));