(function( exports, $ ) {
    var api         = sedApp.editor;

    $( function() {

        api.shortcodeUpdate.sed_product_categories = {
            columns : function( elementId , attrValue  ){
                api.fn.setNumberColumns( attrValue , $("#" + elementId) );
            }
        };

        api.moduleAttrUpdate.woo_category_spacing = function( elementId , attrValue ){
            api.fn.setSpacing( attrValue , $("#" + elementId)  ); 
        };

    });

}(sedApp, jQuery));