(function( exports, $ ) {
    var api = sedApp.editor;

    $( function() {

        api.shortcodeCreate.sed_youtube = function( id , param ){

            param.context.video_url = param.context.url + '?version=' + param.context.version  +
            ( param.context.autoplay ? '&amp;autoplay=1' : '')  +
            (!$.trim(param.context.cc_load_policy) ? '&amp;cc_load_policy='  +  param.context.cc_load_policy : '')  +
            (param.context.color != 'red' ? '&amp;color='  +  param.context.color : '')  +
            (! param.context.controls ? '&amp;controls=0' : '')  +
            ( param.context.disablekb ? '&amp;disablekb=1' : '')  +
            ( param.context.end ? '&amp;end='  +  param.context.end : '')  +
            (param.context.iv_load_policy === '3' ? '&amp;iv_load_policy='  +  param.context.iv_load_policy : '')  +
            ( param.context.loop ? '&amp;loop=1' : '')  +
            ( param.context.modestbranding ? '&amp;modestbranding=1' : '')  +
            (!$.trim(param.context.start) ? '&amp;start='  +  param.context.start : '')  +
            (param.context.theme != 'dark' ? '&amp;theme='  +  param.context.theme : '');

            return param;
        };

    });


}(sedApp, jQuery));