const test=require("tap").test,zlibA=require("zlib"),zlibB=require(".."),crypto=require("crypto");test("zlibA.deflate -> zlibB.inflate",function(a){const b=crypto.randomBytes(1024);zlibA.deflate(b,function(c,d){zlibB.inflate(d,function(c,d){a.same(d,b,"should match"),a.end()})})}),test("zlibB.deflate -> zlibA.inflate",function(a){const b=crypto.randomBytes(1024);zlibB.deflate(b,function(c,d){zlibA.inflate(d,function(c,d){a.same(d,b,"should match"),a.end()})})}),test("zlibB.deflate -> zlibA.inflate (string)",function(a){const b="ohaihihihihihihihihihihihihihihihi";zlibB.deflate(b,function(c,d){zlibA.inflate(d,function(c,d){a.same(d.toString(),b,"should match"),a.end()})})}),test("zlibA.gzip -> zlibB.gunzip",function(a){const b=crypto.randomBytes(1024);zlibA.gzip(b,function(c,d){zlibB.gunzip(d,function(c,d){a.same(d,b,"should match"),a.end()})})}),test("zlibB.gzip -> zlibA.gunzip",function(a){const b=crypto.randomBytes(1024);zlibB.gzip(b,function(c,d){zlibA.gunzip(d,function(c,d){a.same(d,b,"should match"),a.end()})})}),test("zlibB.gzip -> zlibA.gunzip",function(a){const b="lololololoollolololoololololololololololololololololololololol";zlibB.gzip(b,function(c,d){zlibA.gunzip(d,function(c,d){a.same(d.toString(),b,"should match"),a.end()})})});