"use strict";var _=require("lodash");var chalk=require("chalk");var Paginator=module.exports=function(){this.pointer=0;this.lastIndex=0};Paginator.prototype.paginate=function(output,active){var pageSize=7;var lines=output.split("\n");if(lines.length<=pageSize+2){return output}if(this.pointer<3&&this.lastIndex<active&&active-this.lastIndex<9){this.pointer=Math.min(3,this.pointer+active-this.lastIndex)}this.lastIndex=active;var infinite=_.flatten([lines,lines,lines]);var topIndex=Math.max(0,active+lines.length-this.pointer);var section=infinite.splice(topIndex,pageSize).join("\n");return section+"\n"+chalk.dim("(Move up and down to reveal more choices)")};