function convert(val,sourceUnitName,destinationUnitName){destinationUnitName=destinationUnitName||"ms";return val*getUnit(sourceUnitName)/getUnit(destinationUnitName)}function getUnit(unitName){switch(unitName){case"ms":case"millisecond":return 1;case"s":case"second":return 1e3;case"m":case"minute":return 6e4;case"h":case"hour":return 36e5;case"d":case"day":return 864e5;case"w":case"week":return 6048e5;default:throw new Error('"'+unitName+'" is not a valid unit')}}module.exports=convert;