(function( exports, $ ) {
    var api = sedApp.editor;

    $( function() {

        api.shortcodeCreate.sed_3d_carousel = function( id , param ){

            var js_settings_str = "";

            if( $("body").hasClass("rtl-body") ){
                param.context.setting_right_to_left = true;
            }

            $.each( param.context , function( key , value ){
            	if( key.substr(0,7) == 'setting' ){
                    var key_setting = key.substr(8);

                    js_settings_str += "data-trd-carousel-" + key_setting.replace(/\_/gi , "-") + '=\"' + value + '\" ';
            	}

            });

            param.context.item_settings = js_settings_str;

            return param;
        };


    });


}(sedApp, jQuery));