(function( exports, $ ) {
    var api         = sedApp.editor,
        $element    = $('[data-sed-post-role="about-author-module"]')
        settings   = {
            about_author_show_social_profiles         : ".author_social_profiles"
        };

    $( function() {

        $.each( settings , function( key , selector ) {
            api( key , function( value ) {
                value.bind( function( to ) {

                    if( to )
                        $element.find(selector).removeClass("hide");
                    else
                        $element.find(selector).addClass("hide");
                });
            });
        });

    });

}(sedApp, jQuery));