(function( exports, $ ) {
    var api = sedApp.editor;

    $( function() {
                                //api.I18n
        api.Events.bind( "changePreviewMode" , function( mode ){

            if( mode == "off" ){
                $(".module.module-alert").each(function(){
                    var id = $(this).attr("id");
                    if( id && $(this).children().length == 0 ){
                        api.contentBuilder.refreshModule( id );
                    }
                });

            }

        });

        api.shortcodeCreate.sed_alert = function( id , param ){
                            //console.log( "api.I18n.sed_alert_module ---------" , api.I18n.sed_alert_module );
            param.context.I18n = api.I18n.sed_alert_module;

            return param;
        };

    });


}(sedApp, jQuery));