/*global siteEditor:true */
siteEditor.PluginManager.add('archive', function(siteEditor) {
    var api = siteEditor.sedAppClass.editor , $ = siteEditor.dom.Sizzle , _sedArchiveType;
    api.currentHoverEffectModel = api.currentHoverEffectModel || {};

    api.previewer.bind( 'sed_archive_type' , function( type ){
        _sedArchiveType = type;
    });

    api.Events.bind( "after_apply_settings_relations_update" , function( shortcode ){
        if(shortcode == "sed_archive"){   
            if( _sedArchiveType != "page_template" ){
                $("#sed_pb_sed_archive_archive_categories").parents(".row_settings:first").hide();
                $("#sed_pb_sed_archive_posts_per_page").parents(".row_settings:first").hide();
            }
        }
    });

    /*$( function() {


    }); */

});