(function( exports, $ ) {
    var api         = sedApp.editor,
        $element    = $('[data-sed-page-role="page-module-container"]'),
        display_element     = {
            single_page_show_comments                   : ".module-comments",
        };

    $( function() {
       // display and hide item post meta
        $.each( display_element , function( key , selector ) {
            api( key , function( value ) {
                value.bind( function( to ) {

                    if( to )
                        $element.find(selector).removeClass("hide");
                    else
                        $element.find(selector).addClass("hide");

                });
            });
        });



    });
}(sedApp, jQuery));