(function( exports, $ ) {
    var api = sedApp.editor;

    $( function() {
        api.shortcodeUpdate.sed_posts_share = {
            align_icons : function( id , value ){
                       
                var $element = $('[sed_model_id="' + id + '"]');
                    classes = ["ta-l" , "ta-r","ta-c"];

                $.each( classes , function( index , clss ){
                    if( $element.find(".social-navs").hasClass( clss ) && clss != value )
                        $element.find(".social-navs").removeClass( clss );
                    else if( !$element.find(".social-navs").hasClass( clss ) && clss == value )
                        $element.find(".social-navs").addClass( value );
                });

            },
        };
    })

}(sedApp, jQuery));