(function( exports, $ ) {
    var api = sedApp.editor;

    $( function() {



        api.shortcodeUpdate.sed_timeline = {

            number_items : function( id , value ){
                var patternId = $( "#" + id ).siblings(".add-item-timeline:first").attr("id") ,
                    acds = $("#" + id).find(".timeline-wrapper") ,
                    acdsLen = acds.length;

                 api.addModelPattern({
                    newValue        : value  ,
                    oldValue        : acdsLen  ,
                    type            : "single"  ,
                    id              : id  ,
                    patternId       : patternId  ,
                    list            : acds  ,
                    attrs           : [],
                    shortcode_name  : "sed_timeline"  ,
                    refresh         : true ,
                    //group           :   ,
                    //max             :   ,
                    min             : 1
                    //onCreate        : function(){} ,
                    //onAddPatten     : function(){} ,
                 });
            }

        };


    });


}(sedApp, jQuery));